import { SxProps } from '@mui/material';
import { blue, grey } from '@mui/material/colors';


export const createWidgetBtnStyle: SxProps = {
  width: '180px',
  textTransform: 'none',
  fontWeight: 'bold',
  '&:hover': {
    bgcolor: blue[200],
    color: blue[900]
  }
};

export const headerColumnCellStyle: SxProps = {
  fontSize: '15px',
  fontWeight: 'bold',
  borderRight: `1px solid ${grey[300]}`,
  textAlign: 'center'
};

export const widgetTableContainerStyle: SxProps = {
  height: '100px',
  mt: 1
};

export const widgetTableFooterStyle: SxProps = {
  background: `${blue[600]}`,
  fontWeight: 'bold',
  fontSize: '1.3em',
  width: '100%',
  display: 'flex',
  justifyContent: 'center',
  p: 1
};

export const tablePaginationStyle: SxProps = {
  '& .MuiTablePagination-displayedRows': {
	  color:  '#fff',
    fontWeight: 'bold',
    fontSize: '15px',
    width: '85px'
  },
  '& .MuiTablePagination-selectLabel': {
	  display: 'none',
  },
  '& .MuiNativeSelect-select': {
	  display: 'none',
  },
  '& .MuiTablePagination-select': {
	  display: 'none',
  },
  '& .MuiToolbar-root': {
	  padding: 0
  },
  '& .MuiBox-root': {
	  '& .MuiButtonBase-root': {
	    color: '#fff',
      '&:hover': {
        bgcolor: blue[50],
        color: blue[800]
      }
	  },
  },
  '& .css-16c50h-MuiInputBase-root-MuiTablePagination-select': {
	  visibility: 'hidden'
  }
};

export const bookIconStyle: SxProps = { 
  position: 'absolute', 
  width: '35px', 
  height: '30px', 
  color: '#fff', 
  top: 12 
}
